create table xc_orders
(
    order_number  varchar(32)   not null comment '订单号'
        primary key,
    initial_price float(8, 2)   null comment '定价',
    price         float(8, 2)   null comment '交易价',
    start_time    datetime      not null comment '起始时间',
    end_time      datetime      not null comment '结束时间',
    status        varchar(32)   null comment '交易状态',
    user_id       varchar(32)   null comment '用户id',
    details       varchar(1000) null comment '订单明细'
);

